Parameter.Value:
			if (FunctionModel=="Standard")
			{
				write(ConfigOutput02, 0x0000
				| InputMode=="Counter"			? 0x0001 : 0x0000
				| DeliveryValue=="2"			? 0x0008 : 0x0000
				| DeliveryValue=="4"			? 0x0010 : 0x0000
				| MicrostepResolution=="6 Bit"	? 0x0020 : 0x0000
				| MicrostepResolution=="7 Bit"	? 0x0040 : 0x0000
				| MicrostepResolution=="8 Bit"	? 0x0060 : 0x0000
				);

				write(ConfigOutput03, HoldingCurrent);
				write(ConfigOutput04, RatedCurrent);
				write(ConfigOutput05, MaximumCurrent);
				write(ConfigOutput16, MixedDecayConfiguration);

				if(SDCInformation=="on")
				{
					write(ConfigOutput09, 0x00
					| LatchMode=="postive edge R"												? 0x02 : 0x00
					| LatchMode=="negative edge R"												? 0x04 : 0x00
					| LatchSync=="Stepper latch value asynchron (AB/R latch value synchron)"	? 0x08 : 0x00
					);

					write(SdcConfig01, 0x00
					| TriggerEdge01=="falling"			? 0x01 : 0x00
					| SDCSetTimeEnable=="on"			? 0x40 : 0x00
					| SDCInformation=="on"				? 0x80 : 0x00
					);

					if(SDCSetTimeEnable=="on")
					{
						write(DelayedCurrentSwitchOff01, DelayedCurrentSwitchOff01);
					}

					write(MotorSettlingTime01, MotorSettlingTime01);
				}
				else
				{
					write(ConfigOutput09, 0x00
					| LatchEnable=="pos. edge enable Latchfunction AB/R"						? 0x01 : 0x00
					| LatchMode=="postive edge R"												? 0x02 : 0x00
					| LatchMode=="negative edge R"												? 0x04 : 0x00
					| LatchSync=="Stepper latch value asynchron (AB/R latch value synchron)"	? 0x08 : 0x00
					);
				}

				write(ConfigOutput01, StallDetection);
				write(FullStepThreshold01, FullStepThreshold01_conf);
				write(StallDetectMinSpeed01, StallDetectionMinSpeed01);
			}

			if (FunctionModel=="MotionConfiguration")
			{
				write(ConfigOutput02, 0x0000);

				write(ConfigOutput03, HoldingCurrent);
				write(ConfigOutput04, RatedCurrent);
				write(ConfigOutput05, MaximumCurrent);
				write(ConfigOutput16, MixedDecayConfiguration);

				write(ConfigOutput09, 0x00
				| LatchMode=="postive edge R"												? 0x02 : 0x00
				| LatchMode=="negative edge R"												? 0x04 : 0x00
				| LatchSync=="Stepper latch value asynchron (AB/R latch value synchron)"	? 0x08 : 0x00
				);

				write(SdcConfig01, 0xC0
				| TriggerEdge01=="falling"			? 0x01 : 0x00
				);	/* set SDCInformation and SDCSetTimeEnable */

				write(DelayedCurrentSwitchOff01, DelayedCurrentSwitchOff01);

				write(MotorSettlingTime01, MotorSettlingTime01);

				write(ConfigOutput01, StallDetection);
				write(FullStepThreshold01, FullStepThreshold01_conf);
				write(StallDetectMinSpeed01, StallDetectionMinSpeed01);
			}

			if (FunctionModel=="Ramp")
			{
				write(ConfigOutput03a, HoldingCurrent01);
				write(ConfigOutput04a, RatedCurrent01);
				write(ConfigOutput05a, MaximumCurrent01);
				write(FullStepThreshold01, FullStepThreshold01_conf);
				write(MaxSpeed01pos,MaxSpeed01pos_conf);
				write(MaxAcc01,MaxAcc01_conf);
				write(MaxDec01,MaxDec01_conf);
				write(RevLoop01,RevLoop01_conf);
				write(FixedPos01a,FixedPos01a_conf);
				write(FixedPos01b,FixedPos01b_conf);
				write(RefSpeed01,RefSpeed01_conf);
				write(StallRecognitionDelay01,StallRecognitionDelay01_conf);
				write(JoltTime01,JoltTime01_conf);

				if(RefConfig01_selector == "neg. edge")
				{
					write(RefConfig01, 0x82);
				}
				else if(RefConfig01_selector == "pos. edge")
				{
					write(RefConfig01, 0x83);
				}
				else if(RefConfig01_selector == "stall")
				{
					write(RefConfig01, 0x81);
				}
				else if(RefConfig01_selector == "unconditional")
				{
					write(RefConfig01, 0x80);
				}
				else if(RefConfig01_selector == "neg. edge DI4")
				{
					write(RefConfig01, 0x86);
				}
				else if(RefConfig01_selector == "pos. edge DI4")
				{
					write(RefConfig01, 0x87);
				}

				write(StallDetectConfig01, 0x00
				| MixedDecayConfiguration01
				| StallDetection01<<4
				| EnableMotorLoadDisplay=="on"		? 0x80 : 0x00 );

				write(GeneralConfig01, 0x00
				| EnhancedControlword01=="on"		? 0x01 : 0x00
				| MPGenCycleIdx01=="10ms"           ? 0x02 : 0x00
				| MPGenCycleIdx01=="5ms"            ? 0x04 : 0x00 );

				write(LimitSwitchConfig01, 0x00
				| NegLimitSwitch01=="low_active"	? 0x01 : 0x00
				| NegLimitSwitch01=="high_active"	? 0x03 : 0x00
				| PosLimitSwitch01=="low_active"	? 0x04 : 0x00
				| PosLimitSwitch01=="high_active"	? 0x0C : 0x00
				| DirectionControl01=="on"			? 0x80 : 0x00 );

				write(PositionLimitMin01, PositionLimitMin01);
				write(PositionLimitMax01, PositionLimitMax01);

				write(StallDetectMinSpeed01, StallDetectionMinSpeed01);
			}

			if (FunctionModel=="CANIOBusController")
			{
				write(ConfigOutput03a, HoldingCurrent01);
				write(ConfigOutput04a, RatedCurrent01);
				write(ConfigOutput05a, MaximumCurrent01);
				write(FullStepThreshold01, FullStepThreshold01_conf);
				write(MaxSpeed01pos,MaxSpeed01pos_conf);
				write(MaxAcc01,MaxAcc01_conf);
				write(MaxDec01,MaxDec01_conf);
				write(RevLoop01,RevLoop01_conf);
				write(FixedPos01a,FixedPos01a_conf);
				write(FixedPos01b,FixedPos01b_conf);
				write(RefSpeed01,RefSpeed01_conf);
				write(StallRecognitionDelay01,StallRecognitionDelay01_conf);
				write(JoltTime01,JoltTime01_conf);

				if(RefConfig01_selector == "neg. edge")
				{
					write(RefConfig01, 0x82);
				}
				else if(RefConfig01_selector == "pos. edge")
				{
					write(RefConfig01, 0x83);
				}
				else if(RefConfig01_selector == "stall")
				{
					write(RefConfig01, 0x81);
				}
				else if(RefConfig01_selector == "unconditional")
				{
					write(RefConfig01, 0x80);
				}
				else if(RefConfig01_selector == "neg. edge DI4")
				{
					write(RefConfig01, 0x86);
				}
				else if(RefConfig01_selector == "pos. edge DI4")
				{
					write(RefConfig01, 0x87);
				}

				write(StallDetectConfig01, 0x00
				| MixedDecayConfiguration01
				| StallDetection01<<4
				| EnableMotorLoadDisplay=="on"		? 0x80 : 0x00 );

				write(GeneralConfig01, 0x00
				| EnhancedControlword01=="on"		? 0x01 : 0x00
				| MPGenCycleIdx01=="10ms"           ? 0x02 : 0x00
				| MPGenCycleIdx01=="5ms"            ? 0x04 : 0x00 );

				write(LimitSwitchConfig01, 0x00
				| NegLimitSwitch01=="low_active"	? 0x01 : 0x00
				| NegLimitSwitch01=="high_active"	? 0x03 : 0x00
				| PosLimitSwitch01=="low_active"	? 0x04 : 0x00
				| PosLimitSwitch01=="high_active"	? 0x0C : 0x00
				| DirectionControl01=="on"			? 0x80 : 0x00 );

				write(PositionLimitMin01, PositionLimitMin01);
				write(PositionLimitMax01, PositionLimitMax01);

				write(StallDetectMinSpeed01, StallDetectionMinSpeed01);
			}
